  console.log('shutdown signal');
  server.shutdown((hapiErr: Error) => {
    console.log('hapi server shutdown');
    mariadb.shutdown((dbErr: Error) => {
      console.log('mariadb shutdown');
      process.exit(hapiErr || dbErr ? 1 : 0);
    });
  });
}

