// For some reason this can't use the `app` alias but can do relative pathing here... :/
import { HapiRequest } from '../base/server.js';

/**
 * Controllers for Basic "Hello World" Functions
 *
 * See https://github.com/hapijs/hapi/blob/master/API.md#request for HapiRequest definition
 */
export class HelloWorld {
  /**
   * Returns 'Hello World!'
   * @return {String} Hello World!
   */
  static basic(): string {
    return 'Hello World!';
  }

  /**
   * Returns a customized Hello Message
   * @param  {HapiRequest} request Request Parameters
   * @return {String}         Customized Hello Message
   */
  static name(request: HapiRequest): string {

    const name = request.params.name;

    return name + ' says: ' + Math.random() + '!!!!';
  }
}

export default [
  {
    path: '/hello',
    method: 'GET',
    controller: HelloWorld.basic
  },
  {
    path: '/{name}',
    method: 'GET',
    controller: HelloWorld.name
  }
];

import { HelloWorld } from './hello.controller.js';

import { describe, it } from 'mocha';
import { expect } from 'chai';

/** @test {HelloWorld} */
describe('HelloWorld', () => {
  /** @test {HelloWorld.basic} */
  describe('basic()', () => {
    it('returns \'Hello World!\'', () => {
      const returned = HelloWorld.basic();

      expect(returned).to.equal('Hello World!');
    });
  });

  /** @test {HelloWorld.name} */
  describe('name()', () => {
    it('returns \'{request.params.name} says:\'', () => {
      const request = {
        params: {
          name: 'Devlin'
        }
      };

      let returned = HelloWorld.name(request);
      expect(returned.startsWith('Devlin says')).to.be.true;

      request.params.name = 'Michelle';

      returned = HelloWorld.name(request);
      expect(returned.startsWith('Michelle says')).to.be.true;
    });
  });
});

